strings.body:
trim_string() {
    # Usage: trim_string "   example   string    "
    : "${1#"${1%%[![:space:]]*}"}"
    : "${_%"${_##*[![:space:]]}"}"
    printf '%s\n' "\$_"
}

split() {
   # Usage: split "string" "delimiter"
   IFS=$'\n' read -d "" -ra arr <<< "${1//\$2/$'\n'}"
   printf '%s\n' "${arr[@]}"
}

arrays.body:
reverse_array() {
    # Usage: reverse_array "array"
    shopt -s extdebug
    f()(printf '%s\n' "${BASH_ARGV[@]}"); f "$@"
    shopt -u extdebug
}

random_array_element() {
    # Usage: random_array_element "array"
    local arr=("$@")
    printf '%s\n' "${arr[RANDOM % $#]}"
}

file handling.body:
head() {
    # Usage: head "n" "file"
    mapfile -tn "\$1" line < "\$2"
    printf '%s\n' "${line[@]}"
}

tail() {
    # Usage: tail "n" "file"
    mapfile -tn 0 line < "\$2"
    printf '%s\n' "${line[@]: -\$1}"
}

lines() {
    # Usage: lines "file"
    mapfile -tn 0 lines < "\$1"
    printf '%s\n' "${#lines[@]}"
}

file paths.body:
dirname() {
    # Usage: dirname "path"
    local tmp=\${1:-.}

    [[ \$tmp != *[!/]* ]] && {
        printf '/\n'
        return
    }

    tmp=${tmp%%"${tmp##*[!/]}"}

    [[ \$tmp != */* ]] && {
        printf '.\n'
        return
    }

    tmp=${tmp%/*}
    tmp=${tmp%%"${tmp##*[!/]}"}

    printf '%s\n' "${tmp:-/}"
}

basename() {
    # Usage: basename "path" ["suffix"]
    local tmp

    tmp=${1%"${1##*[!/]}"}
    tmp=${tmp##*/}
    tmp=${tmp%"${2/"\$tmp"}"}

    printf '%s\n' "${tmp:-/}"
}

brace expansion.body:
# Syntax: {<START>..<END>}

# Print numbers 1-100.
echo {1..100}

# Print range of floats.
echo 1.{1..9}

# Print chars a-z.
echo {a..z}
echo {A..Z}

# Nesting.
echo {A..Z}{0..9}

# Print zero-padded numbers.
# CAVEAT: bash 4+
echo {01..100}

# Change increment amount.
# Syntax: {<START>..<END>..<INCREMENT>}
# CAVEAT: bash 4+
echo {1..10..2} # Increment by 2.

echo {apples,oranges,pears,grapes}

